package model;
import java.time.*;
public class Task{private final String id;private String desc;private LocalTime start;private LocalTime end;private Priority pr;private boolean done;public enum Priority{LOW,MEDIUM,HIGH}public Task(String id,String d,LocalTime s,LocalTime e,Priority p){if(s==null||e==null||s.isAfter(e))throw new IllegalArgumentException("Invalid time");this.id=id;this.desc=d;this.start=s;this.end=e;this.pr=p;this.done=false;}public String getId(){return id;}public String getDescription(){return desc;}public LocalTime getStart(){return start;}public LocalTime getEnd(){return end;}public Priority getPriority(){return pr;}public boolean isCompleted(){return done;}public void setCompleted(boolean c){done=c;}public boolean overlaps(Task o){return !(this.end.isBefore(o.start)||this.start.isAfter(o.end));}@Override public String toString(){return start+"-"+end+": "+desc+" ["+pr+"]"+(done?" (done)":"");}}
